import React, { Component } from 'react'
import ReactDOM from 'react-dom';
import { Container, Row, Col, Card} from 'react-bootstrap'
import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader
import axios from 'axios'
import { Carousel } from 'react-responsive-carousel';
import EmployeeService from '../services/EmployeeService'
import Image from './la.jpg'
import Image1 from './chicago.jpg'



class HomeComponent extends Component {

    

    constructor(props) {
        super(props)


        this.state = {
                employees: [],
                tweets: []
        }
    
    }

    componentDidMount(){
        EmployeeService.getEmployees().then((res) => {
            this.setState({ employees: res.data});
        });


        axios.get(`https://baylor-board.herokuapp.com/tweets`)
      .then(res => {
        const tweets = res.data;
        this.setState({ tweets });
      })
    }

    


    render() {
        return (
        

<Carousel autoPlay interval="5000" transitionTime="9000" infiniteLoop = "true" >

                

               


                <div>
                    <img src={Image} />
                </div>
                <div>
                    <img src={Image1} />
                </div>

                
                <div>
                   
<Row className='card-container'>
            this.state.tweets.map(
                                        tweet => 
              <Col lg={4} md={6} key={tweet.id}>
                <Card>
                  <Card.Body>
                    <Card.Title className='mb-2 text-dark'>
                      UserName : {tweet.user}
                    </Card.Title>
                    <Card.Text className='text-dark'>
                      Tweet: <span>{tweet.text}</span>
                    </Card.Text>
                    <Card.Subtitle className='text-dark'>
                      Status: {tweet.status}
                    </Card.Subtitle>
                  </Card.Body>
                </Card>
              </Col>
            ))}
          </Row>


                </div>


                 <div>
 <h2 className="text-center"> Recent Events </h2>
<div className = "row">
                        <table className = "table table-striped table-bordered">

                            <thead>
                                <tr>
                                    <th> Event Title </th>
                                    <th> Event Coordinator </th>
                                    <th> Event Description </th>
                                    <th> Actions </th>
                                </tr>
                            </thead>
                            <tbody>
                                {
                                    this.state.employees.map(
                                        employee => 
                                        <tr key = {employee.id}>
                                             <td> { employee.title} </td>   
                                             <td> {employee.coordinator}</td>
                                             <td> {employee.description}</td>
                                             <td>
                                                 <button style={{marginLeft: "10px"}} onClick={ () => this.viewEmployee(employee.id)} className="btn btn-info">View </button>
                                             </td>
                                        </tr>
                                    )
                                }
                            </tbody>
                        </table>

                 </div>

                </div>

                

            </Carousel>




        )
    }
}

export default HomeComponent
